import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { hentInitiativById, oppdaterInitiativStatus } from '../services/initiativService';

interface Initiativ {
  _id: string;
  initiativId: string;
  tittel: string;
  beskrivelse: string;
  maal: string;
  status: string;
  ansvarlig: string;
  prioritet: string;
  startDato: string;
  sluttDato?: string;
  kommentarer?: string;
  vedlegg: Array<{
    _id: string;
    type: 'link' | 'file';
    navn: string;
    url?: string;
    filsti?: string;
  }>;
  handlinger: Array<{
    _id: string;
    beskrivelse: string;
    ansvarlig?: string;
    frist?: string;
    status: 'Ikke startet' | 'Pågår' | 'Fullført';
  }>;
}

const statusFarger: Record<string, string> = {
  'Ide': 'bg-gray-100 text-gray-800',
  'Mockup': 'bg-blue-100 text-blue-800',
  'PoC': 'bg-purple-100 text-purple-800',
  'Utvikling': 'bg-yellow-100 text-yellow-800',
  'Implementert': 'bg-green-100 text-green-800',
  'Avsluttet': 'bg-red-100 text-red-800'
};

const statusAlternativer = ['Ide', 'Mockup', 'PoC', 'Utvikling', 'Implementert', 'Avsluttet'];

const InitiativDetaljer: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [initiativ, setInitiativ] = useState<Initiativ | null>(null);
  const [laster, setLaster] = useState<boolean>(true);
  const [feil, setFeil] = useState<string | null>(null);
  const [redigererStatus, setRedigererStatus] = useState<boolean>(false);
  const [nyStatus, setNyStatus] = useState<string>('');

  useEffect(() => {
    const lastInitiativ = async () => {
      try {
        setLaster(true);
        if (id) {
          const data = await hentInitiativById(id);
          setInitiativ(data);
          setNyStatus(data.status);
        }
      } catch (error) {
        setFeil('Kunne ikke laste initiativ. Vennligst prøv igjen senere.');
      } finally {
        setLaster(false);
      }
    };

    lastInitiativ();
  }, [id]);

  const handleStatusEndring = async () => {
    if (!initiativ || nyStatus === initiativ.status) {
      setRedigererStatus(false);
      return;
    }

    try {
      const oppdatertInitiativ = await oppdaterInitiativStatus(initiativ._id, nyStatus);
      setInitiativ(oppdatertInitiativ);
      setRedigererStatus(false);
    } catch (error) {
      setFeil('Kunne ikke oppdatere status. Vennligst prøv igjen senere.');
    }
  };

  const formaterDato = (datoStreng: string) => {
    const dato = new Date(datoStreng);
    return dato.toLocaleDateString('nb-NO', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  if (laster) {
    return (
      <div className="text-center py-10">
        <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-primary-500 border-t-transparent"></div>
        <p className="mt-2 text-gray-600">Laster initiativ...</p>
      </div>
    );
  }

  if (feil) {
    return (
      <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md">
        {feil}
      </div>
    );
  }

  if (!initiativ) {
    return (
      <div className="text-center py-10">
        <p className="text-gray-600">Initiativ ikke funnet.</p>
        <Link to="/" className="btn btn-primary mt-4 inline-block">
          Tilbake til oversikten
        </Link>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-6 flex justify-between items-center">
        <div>
          <div className="flex items-center gap-2">
            <h1 className="text-2xl font-bold text-gray-900">{initiativ.tittel}</h1>
            <span className="text-sm font-medium text-gray-500">({initiativ.initiativId})</span>
          </div>
          <p className="text-gray-600 mt-1">Ansvarlig: {initiativ.ansvarlig}</p>
        </div>
        <Link to="/" className="btn btn-outline">
          Tilbake til oversikten
        </Link>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <div className="card mb-6">
            <h2 className="text-xl font-semibold mb-4">Beskrivelse</h2>
            <p className="whitespace-pre-wrap">{initiativ.beskrivelse}</p>
          </div>

          <div className="card mb-6">
            <h2 className="text-xl font-semibold mb-4">Mål</h2>
            <p className="whitespace-pre-wrap">{initiativ.maal}</p>
          </div>

          {initiativ.kommentarer && (
            <div className="card mb-6">
              <h2 className="text-xl font-semibold mb-4">Kommentarer</h2>
              <p className="whitespace-pre-wrap">{initiativ.kommentarer}</p>
            </div>
          )}

          {initiativ.vedlegg.length > 0 && (
            <div className="card mb-6">
              <h2 className="text-xl font-semibold mb-4">Vedlegg</h2>
              <ul className="divide-y">
                {initiativ.vedlegg.map((vedlegg) => (
                  <li key={vedlegg._id} className="py-3 first:pt-0 last:pb-0">
                    {vedlegg.type === 'link' ? (
                      <a
                        href={vedlegg.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-primary-600 hover:underline flex items-center"
                      >
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          className="h-5 w-5 mr-2"
                          fill="none"
                          viewBox="0 0 24 24"
                          stroke="currentColor"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M13.828 10.172a4 4 0 00-5.656 0l-4 4a4 4 0 105.656 5.656l1.102-1.101"
                          />
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M10.172 13.828a4 4 0 005.656 0l4-4a4 4 0 10-5.656-5.656l-1.102 1.101"
                          />
                        </svg>
                        {vedlegg.navn}
                      </a>
                    ) : (
                      <span className="flex items-center text-gray-700">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          className="h-5 w-5 mr-2"
                          fill="none"
                          viewBox="0 0 24 24"
                          stroke="currentColor"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13"
                          />
                        </svg>
                        {vedlegg.navn}
                      </span>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="lg:col-span-1">
          <div className="card mb-6">
            <h2 className="text-xl font-semibold mb-4">Status</h2>
            {redigererStatus ? (
              <div>
                <div className="flex flex-wrap gap-2 mb-4">
                  {statusAlternativer.map((status) => (
                    <button
                      key={status}
                      onClick={() => setNyStatus(status)}
                      className={`px-3 py-1.5 text-sm font-medium rounded-full transition-colors ${
                        nyStatus === status
                          ? 'bg-primary-600 text-white'
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      {status}
                    </button>
                  ))}
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={handleStatusEndring}
                    className="btn btn-primary"
                  >
                    Lagre
                  </button>
                  <button
                    onClick={() => {
                      setRedigererStatus(false);
                      setNyStatus(initiativ.status);
                    }}
                    className="btn btn-outline"
                  >
                    Avbryt
                  </button>
                </div>
              </div>
            ) : (
              <div>
                <div className="flex items-center mb-4">
                  <span
                    className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                      statusFarger[initiativ.status] || 'bg-gray-100 text-gray-800'
                    }`}
                  >
                    {initiativ.status}
                  </span>
                </div>
                <button
                  onClick={() => setRedigererStatus(true)}
                  className="btn btn-outline"
                >
                  Endre status
                </button>
              </div>
            )}
          </div>

          <div className="card mb-6">
            <h2 className="text-xl font-semibold mb-4">Detaljer</h2>
            <dl className="divide-y">
              <div className="py-2 flex justify-between">
                <dt className="text-gray-500">Prioritet</dt>
                <dd className="font-medium">{initiativ.prioritet}</dd>
              </div>
              <div className="py-2 flex justify-between">
                <dt className="text-gray-500">Startdato</dt>
                <dd className="font-medium">{formaterDato(initiativ.startDato)}</dd>
              </div>
              {initiativ.sluttDato && (
                <div className="py-2 flex justify-between">
                  <dt className="text-gray-500">Forventet ferdigstillelse</dt>
                  <dd className="font-medium">{formaterDato(initiativ.sluttDato)}</dd>
                </div>
              )}
            </dl>
          </div>

          {initiativ.handlinger.length > 0 && (
            <div className="card">
              <h2 className="text-xl font-semibold mb-4">Handlinger</h2>
              <ul className="divide-y">
                {initiativ.handlinger.map((handling) => (
                  <li key={handling._id} className="py-3 first:pt-0 last:pb-0">
                    <div className="flex justify-between items-start mb-1">
                      <span className="font-medium">{handling.beskrivelse}</span>
                      <span
                        className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${
                          handling.status === 'Fullført'
                            ? 'bg-green-100 text-green-800'
                            : handling.status === 'Pågår'
                            ? 'bg-yellow-100 text-yellow-800'
                            : 'bg-gray-100 text-gray-800'
                        }`}
                      >
                        {handling.status}
                      </span>
                    </div>
                    {handling.ansvarlig && (
                      <p className="text-sm text-gray-600">Ansvarlig: {handling.ansvarlig}</p>
                    )}
                    {handling.frist && (
                      <p className="text-sm text-gray-600">Frist: {formaterDato(handling.frist)}</p>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InitiativDetaljer;
