import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import InitiativKort from '../components/InitiativKort';
import StatusFilter from '../components/StatusFilter';
import { hentAlleInitiativer } from '../services/initiativService';

interface Initiativ {
  _id: string;
  initiativId: string;
  tittel: string;
  beskrivelse: string;
  status: string;
  ansvarlig: string;
  prioritet: string;
  startDato: string;
  sluttDato?: string;
}

const Dashboard: React.FC = () => {
  const [initiativer, setInitiativer] = useState<Initiativ[]>([]);
  const [filtrerteInitiativer, setFiltrerteInitiativer] = useState<Initiativ[]>([]);
  const [laster, setLaster] = useState<boolean>(true);
  const [feil, setFeil] = useState<string | null>(null);
  const [aktivFilter, setAktivFilter] = useState<string>('Alle');

  useEffect(() => {
    const lastInitiativer = async () => {
      try {
        setLaster(true);
        const data = await hentAlleInitiativer();
        setInitiativer(data);
        setFiltrerteInitiativer(data);
        setLaster(false);
      } catch (error) {
        setFeil('Kunne ikke laste initiativer. Vennligst prøv igjen senere.');
        setLaster(false);
      }
    };

    lastInitiativer();
  }, []);

  useEffect(() => {
    if (aktivFilter === 'Alle') {
      setFiltrerteInitiativer(initiativer);
    } else {
      setFiltrerteInitiativer(
        initiativer.filter((initiativ) => initiativ.status === aktivFilter)
      );
    }
  }, [aktivFilter, initiativer]);

  const handleFilterChange = (filter: string) => {
    setAktivFilter(filter);
  };

  return (
    <div>
      <div className="mb-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">KI-Initiativer</h1>
        <Link to="/initiativer/ny" className="btn btn-primary">
          Nytt Initiativ
        </Link>
      </div>

      <StatusFilter aktivFilter={aktivFilter} onFilterChange={handleFilterChange} />

      {laster ? (
        <div className="text-center py-10">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-4 border-primary-500 border-t-transparent"></div>
          <p className="mt-2 text-gray-600">Laster initiativer...</p>
        </div>
      ) : feil ? (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md">
          {feil}
        </div>
      ) : filtrerteInitiativer.length === 0 ? (
        <div className="text-center py-10">
          <p className="text-gray-600">Ingen initiativer funnet.</p>
          <Link to="/initiativer/ny" className="btn btn-primary mt-4 inline-block">
            Opprett ditt første initiativ
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtrerteInitiativer.map((initiativ) => (
            <InitiativKort key={initiativ._id} initiativ={initiativ} />
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
